import { BsTrophyFill } from "react-icons/bs";
import { BsFillCameraVideoFill, BsFillAwardFill } from "react-icons/bs";
import RatingCard from "../RatingCard/RatingCard";

function CourseInfoComponent(props) {
  return (
    <>
      <Container>
        <div className="course-info-row">
          <Col id="course-info">
            <h2>{props.title}</h2>
            <h5>{props.category}</h5>
            <h6>{props.author}</h6>
            <h7>
              <div className="certificate-container">
                <BsFillAwardFill size="1em" style={{ color: "#CFB53B" }} />{" "}
                Certificado
              </div>
            </h7>
            <ReactStars value={props.rating} edit={false} size={18} />
            <h7>999 vendidos</h7>

            <h6 style={{ fontSize: "24px", color: "#6CB1CF" }}>
              R$ {props.price}
