        <hr />
        <Row className="course-info-row">
          <Container fluid="md">
            <Row>
              <Col>
                <h2>Avaliações</h2>
                <p>Veja o que estão falando sobre esse curso</p>

                {/* {this.state.ratingComents?.map((item) => (
