import { Nav, Navbar, NavDropdown } from "react-bootstrap";
import { Container } from "react-bootstrap";
import { Form, FormControl } from "react-bootstrap";
import { BsFillCartFill } from "react-icons/bs";

import "../Navbar/Navbar.style.css";

function NavbarComponent() {
  const categorias = [
    {
      id: 11,
      nome: "Cardiologia",
    },
    {
      id: 22,
      nome: "Geriatria",
    },
    {
      id: 33,
      nome: "Patologia",
    },
    {
      id: 44,
      nome: "Infectologia",
    },
  ];

  const user = true;

  return (
    <>
      <div>
        <Navbar expand="lg" id="navbar">
          <Container fluid="md" id="container-nav">
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Brand href="/" id="nav-brand">
              Presper Daher
            </Navbar.Brand>

            <Navbar.Collapse id="basic-navbar-nav">
              <div className="navbar-items ">
                <Nav className="me-auto" id="teste">
                  <NavDropdown title="Categorias" id="basic-nav-dropdown">
                    {categorias.map((item) => {
                      return (
                        <li key={item.id}>
                          <NavDropdown.Item href={`/`}>
                            {item.nome}
                          </NavDropdown.Item>
                        </li>
                      );
                    })}
                  </NavDropdown>
                  <Form>
                    <FormControl
                      type="search"
                      placeholder="Pesquisar cursos"
                      className="me-2"
                      aria-label="Search"
                      id="search-bar"
                      onKeyPress={() => console.log("Searching...")}
                    />
                  </Form>

                  <Nav.Link
                    href="/"
                    style={{ justifyContent: "center" }}
                    id="cart-link"
                  >
                    <BsFillCartFill style={{ fontSize: "24px" }} />
                  </Nav.Link>

                  <Nav.Link
                    href="/signup"
                    className="nav-link"
                    id="signup-link"
                  >
                    Cadastre-se
                  </Nav.Link>

                  {!user ? (
                    <Nav.Link
                      href="/login"
                      className="nav-link"
                      id="login-link"
                    >
                      Fazer Login
                    </Nav.Link>
                  ) : (
                    <NavDropdown title="Minha Conta" id="basic-nav-dropdown">
                      <NavDropdown.Item href={`/perfil`}>
                        Perfil
                      </NavDropdown.Item>
                      <NavDropdown.Item href={`/my-courses`}>
                        Meus Cursos
                      </NavDropdown.Item>
                      <NavDropdown.Item href={`/my-certificates`}>
                        Certificados
                      </NavDropdown.Item>
                      <NavDropdown.Item href={`/my-payment-info`}>
                        Formas de Pagamento
                      </NavDropdown.Item>
                      <NavDropdown.Item href={`/`}>Sair</NavDropdown.Item>
                    </NavDropdown>
                  )}
                </Nav>
              </div>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </div>
    </>
  );
}

export default NavbarComponent;
