import { useContext, useEffect } from "react";
import { CartContext } from "../../contexts/CartContext/CartContext";

import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

import ProductCard from "../../componentes/ProductCard/ProductCard";

import { useState } from "react";
import "./ShoppingCart.styles.css";

function ShoppingCart() {
  const [prodRemoved, isProdRemoved] = useState(false);
  const { cart, removeItemFromCart, getCart } = useContext(CartContext);

  const [prods, setProds] = useState(cart);

  return (
    <>
      <Container id="main-container">
        <div>
          <Row className="page-title">
            <Col>
              <h2>Meu carrinho</h2>
              <p>Vizualize os produtos adicionados ao seu carrinho de compra</p>
            </Col>
          </Row>
        </div>
        <hr />
        <div className="container-content">
          <div className="container-prods">
            {/* {prods?.map((prod, id) => {
              return (
                <ProductCard
                  key={id}
                  title={prod.title}
                  author={prod.author}
                  category={prod.category}
                  price={prod.price}
                  onClick={() => {
                    removeItemFromCart(prod.id);
                    isProdRemoved(true);
                  }}
                />
              );
            })} */}

            <ProductCard
              title={"Bases Patológicas"}
              author={"Ricardo Gomes"}
              category={"Patologia"}
              price={"299,00"}
              onClick={() => {
                // removeItemFromCart(prod.id);
                isProdRemoved(true);
              }}
            />
          </div>
          <div className="container-checkout">
            <div className="checkout-card">
              <div className="title">Checkout</div>
              <div className="prod-list">
                <div id="prod-tite"></div>
                <div></div>
              </div>
            </div>
          </div>
        </div>
      </Container>
    </>
  );
}

export default ShoppingCart;
