    <>
      <Container>
        <Container fluid>
          <div className="container-item">
            <Row className="row-novo-curso">
              <Col>
                <h2>Minhas Formas de Pagamento</h2>
                <p>Vizualize suas formas de pagamento cadastradas</p>
              </Col>
              <Col>
                <Button
                  id="btn-add"
                  onClick={() => {
                    setAddCreditCardModalShow(true);
                  }}
                >
                  ADICIONAR CARTÃO
                </Button>
              </Col>
            </Row>
          </div>
          <hr></hr>
          <div className="container-item">
